`#pragma once
#include <CrySchematyc/ResourceTypes.h>
#include <CrySchematyc/MathTypes.h>

class CTimeOfDayComponent final :public IEntityComponent {
public:
	CTimeOfDayComponent() = default;
	virtual ~CTimeOfDayComponent() {}

	virtual void Initialize() override;
	virtual void ProcessEvent(SEntityEvent &event) override;
	virtual uint64 GetEventMask() const override;

	static void ReflectType(Schematyc::CTypeDesc<CTimeOfDayComponent>& desc);

protected:
	bool m_bUpdateForce;
	bool m_bActive;
	Schematyc::Range<0, 3> m_bSpeed = 0.2f;
	Schematyc::Range<0, 24> m_bStartTime = 12.f;
};`

- cpp file

`#include "StdAfx.h"
#include "TimeOfDay.h"
#include "Cry3DEngine/ITimeOfDay.h"

#include <CrySchematyc/Env/IEnvRegistrar.h>
#include <CrySchematyc/Env/Elements/EnvComponent.h>


static void RegisterCTimeOfDayComponent(Schematyc::IEnvRegistrar &registra) {
	Schematyc::CEnvRegistrationScope scope = registra.Scope(IEntity::GetEntityScopeGUID());
	{
		Schematyc::CEnvRegistrationScope componentScope = scope.Register(SCHEMATYC_MAKE_ENV_COMPONENT(CTimeOfDayComponent));
	}
}
CRY_STATIC_AUTO_REGISTER_FUNCTION(&RegisterCTimeOfDayComponent)

void CTimeOfDayComponent::Initialize()
{
	if (!m_bActive) {
		FreeEntitySlot();
		return;
	}
	if (!m_bUpdateForce)
		return;
	ITimeOfDay *pTOD = gEnv->p3DEngine->GetTimeOfDay();
	if (!pTOD)
		return;
	pTOD->SetTime(m_bStartTime, m_bUpdateForce);
	ITimeOfDay::SAdvancedInfo info;
	info.fAnimSpeed = m_bSpeed;
	pTOD->SetAdvancedInfo(info);
}

void CTimeOfDayComponent::ProcessEvent(SEntityEvent & event)
{
	if (event.event == ENTITY_EVENT_COMPONENT_PROPERTY_CHANGED)
		Initialize();
	if (event.event == ENTITY_EVENT_START_GAME)
		Initialize();
}

uint64 CTimeOfDayComponent::GetEventMask() const
{
	return BIT64(ENTITY_EVENT_COMPONENT_PROPERTY_CHANGED) | BIT64(ENTITY_EVENT_START_GAME);
}

void CTimeOfDayComponent::ReflectType(Schematyc::CTypeDesc<CTimeOfDayComponent>& desc)
{
	desc.SetGUID("{F2134C7A-08E8-4967-AD7E-47AF5C8B7C42}"_cry_guid);
	desc.SetEditorCategory("Enviroment");
	desc.SetLabel("Time of day");
	desc.SetDescription("play time of day cicle");
	desc.AddMember(&CTimeOfDayComponent::m_bActive, 'acte', "Active", "Active", "Active Time of day function", true);
	desc.AddMember(&CTimeOfDayComponent::m_bUpdateForce, 'udfc', "Update Force", "Update Force", "Update Force", true);
	desc.AddMember(&CTimeOfDayComponent::m_bStartTime, 'strt', "Start Time", "Start Time", "Start Time", 12.f);
	desc.AddMember(&CTimeOfDayComponent::m_bSpeed, 'sped', "Speed", "Speed", "Speed", 0.2f);
	desc.SetComponentFlags({ IEntityComponent::EFlags::Transform , IEntityComponent::EFlags::Socket, IEntityComponent::EFlags::Attach });
}
