I want to add a camera differ from the main camera to render the 3d world,so I write a FG node like this.Everything is ok in sandbox but crashing in luncher.

class CFlowNode_CameraRenderToViewport : public CFlowBaseNode<eNCT_Singleton>, public IGameFrameworkListener
{
private:
	IRenderer* m_pRenderer;
	ISystem* m_pSystem;
	CCamera* m_pCamera;
	bool m_enabled;
	int m_renderTarget;
	float m_posX;
	float m_posY;
	float m_width;
	float m_height;
public:
	CFlowNode_CameraRenderToViewport(SActivationInfo* pActInfo)
		:m_pSystem(gEnv->pSystem)
		, m_pRenderer(gEnv->pRenderer)
		, m_pCamera(nullptr)
	{
		m_enabled = false;
		m_renderTarget = 0;
		m_posX = 0.f;
		m_posY = 0.f;
		m_width = 128.f;
		m_height = 128.f;
	}

	~CFlowNode_CameraRenderToViewport()
	{
		if (m_enabled)
		{
			gEnv->pRenderer->DestroyRenderTarget(m_renderTarget);
		}
	}

	virtual void GetMemoryUsage(ICrySizer* s) const
	{
		s->Add(*this);
	}

	enum EInputPorts
	{
		EIP_Start = 0,
		EIP_Stop,
		EIP_X,
		EIP_Y,
		EIP_Width,
		EIP_Height,
		EIP_Camera,
	};

	virtual void GetConfiguration(SFlowNodeConfig& config)
	{
		static const SInputPortConfig in_config[] = {
			InputPortConfig_Void("Start", ""),
			InputPortConfig_Void("Stop", ""),
			InputPortConfig<int>("X", 0, _HELP("x")),
			InputPortConfig<int>("Y", 0, _HELP("y")),
			InputPortConfig<int>("Width", 128, _HELP("Width")),
			InputPortConfig<int>("Height", 128, _HELP("Height")),
			InputPortConfig<EntityId>("Camera", -1, _HELP("CameraEntityID")),
			{ 0 }
		};

		config.pInputPorts = in_config;
		config.pOutputPorts = 0;
		config.sDescription = _HELP("");
		config.nFlags = 0;
		config.SetCategory(EFLN_APPROVED);
	}

	virtual void ProcessEvent(EFlowEvent event, SActivationInfo* pActInfo)
	{
		switch (event)
		{
		case eFE_Initialize:
		{
			m_pRenderer->DestroyRenderTarget(m_renderTarget);
		}
		break;
		case eFE_Activate:
		{
			if (IsPortActive(pActInfo, EIP_Start))
			{
				if (GetPortEntityId(pActInfo, EIP_Camera) == -1)
				{
					return;
				}
				IEntity* pCameraEntity = gEnv->pEntitySystem->GetEntity(GetPortEntityId(pActInfo, EIP_Camera));
				IEntityCameraComponent* pCameraProxy = (IEntityCameraComponent*)pCameraEntity->GetProxy(ENTITY_PROXY_CAMERA);
				if (pCameraProxy == nullptr)
				{
					return;
				}
				m_pCamera = &pCameraProxy->GetCamera();
				m_pCamera->SetJustActivated(true);

				gEnv->pSystem->AddOtherCamera(m_pCamera);

				m_posX = GetPortFloat(pActInfo, EIP_X);
				m_posY = GetPortFloat(pActInfo, EIP_Y);
				m_width = GetPortFloat(pActInfo, EIP_Width);
				m_height = GetPortFloat(pActInfo, EIP_Height);

				int width = m_pRenderer->GetWidth();
				int height = m_pRenderer->GetHeight();
				m_renderTarget = m_pRenderer->CreateRenderTarget(width, height, Clr_Unknown, eTF_R8G8B8A8);

				gEnv->pGameFramework->RegisterListener(this, "canera_listener", FRAMEWORKLISTENERPRIORITY_MENU);

				m_enabled = true;
			}

			if (IsPortActive(pActInfo, EIP_Stop))
			{
				m_enabled = false;
				m_pRenderer->DestroyRenderTarget(m_renderTarget);
				gEnv->pGameFramework->UnregisterListener(this);
			}
		}
		break;
		}
	}
	// IGameFrameworkListener
	void RenderToTarget()
	{
		if (m_enabled)
		{
			m_pRenderer->SetRenderTarget(m_renderTarget, SRF_USE_ORIG_DEPTHBUF); 

			int renderFlag = SHDF_ZPASS | SHDF_ALLOWHDR | SHDF_ALLOWPOSTPROCESS | SHDF_ALLOW_WATER | SHDF_ALLOW_AO;
			SRenderingPassInfo passInfo = SRenderingPassInfo::CreateGeneralPassRenderingInfo(*m_pCamera);
			gEnv->p3DEngine->RenderWorld(renderFlag, passInfo, "cam_try1");

			m_pRenderer->SetRenderTarget(0, SRF_USE_ORIG_DEPTHBUF);

		}
	}

	virtual void OnPostUpdate(float fDeltaTime)
	{
		if (m_enabled)
		{
			RenderToTarget();
			m_pRenderer->Draw2dImage(m_posX, m_posY, m_width, m_height, m_renderTarget, 1, 0, 0, 1, 180, 1, 1, 1, 1, 1);
		}
	}

	virtual void OnSaveGame(ISaveGame* pSaveGame) override {}
	virtual void OnLoadGame(ILoadGame* pLoadGame) override {}
	virtual void OnLevelEnd(const char* nextLevel) override {}
	virtual void OnActionEvent(const SActionEvent& event) override {};
	// ~IGameFrameworkListener
};
REGISTER_FLOW_NODE("Camera:RenderToViewport", CFlowNode_CameraRenderToViewport);
