                HorizontalGroup container = new HorizontalGroup();
                String ordername = o.getObjectId();
                Label offerLabel = new Label(ordername, uiSkin);
                container.addActor(offerLabel);
                TextField txtBid = new TextField("", uiSkin);
                container.addActor(txtBid);
                TextButton btn = new TextButton("Bid", uiSkin);
                container.addActor(btn);
                offerGroup.addActor(container);
                btn.addListener(new ClickListener() {
                    @Override
                    public void clicked(InputEvent event, float x, float y) {
                        onClickBid(o, txtBid, container);
                    }
                });

            }
        }
    }```

