public static SequenceAction createSequencesOfActions(String[] actionNames, Object[] actionParameters)
{
    SequenceAction sequenceAction = new SequenceAction();
    int actionCounter = 0;
    for(String actionName : actionNames)
    {
        if(actionName.equalsIgnoreCase("moveTo"))
        {

            Tuple<Vector2, Float> moveToParameters = (Tuple<Vector2, Float>) actionParameters[actionCounter];
