public class GDXMessageTransport implements HttpResponseListener {
    private static final String GDX_API_PROXY_URL = "http://localhost:18080/gdx-entry-point-proxy/";

//CALL:
    public void establishConnection() {
        HttpRequest httpRequest = new HttpRequest();
        httpRequest.setMethod(HttpMethods.GET);
        httpRequest.setUrl(GDX_API_PROXY_URL);
        Gdx.net.sendHttpRequest(httpRequest, this);
    }

    @Override
    public void handleHttpResponse(HttpResponse httpResponse) {
        L.d("httpResponse", httpResponse);
        HttpStatus status = httpResponse.getStatus();
        L.d("status", status);
        int statusCode = status.getStatusCode();
        L.d("statusCode", statusCode);

        byte[] result = httpResponse.getResult();
        L.d("result", result);
        if (result != null) {
            L.d("result.length", result.length);
        }
        String string_result = httpResponse.getResultAsString();
        L.d("string_result", string_result);

        String header_set_cookie = httpResponse.getHeader("Set-Cookie");
        L.d("header_set_cookie", header_set_cookie);


        Map<String, List<String>> headers = httpResponse.getHeaders();
        print_map("map", headers);
        throw new Error("EXIT HERE");
    }

    private static void print_map(String name, Map<?, ?> value) {
        Set<?> keys = value.keySet();
        Iterator<?> i = keys.iterator();
        L.d("---" + name + "[" + value.size() + "]---");
        int k = 0;
        while (i.hasNext()) {
            Object key = i.next();
            Object val = value.get(key);
            L.d("   [" + k + "] " + key + " :-> " + val);
            k++;
        }
    }

    @Override
    public void failed(Throwable t) {
        t.printStackTrace();
    }

    @Override
    public void cancelled() {
        L.d("cancelled()");
    }

}
