Scene@ scene_;
Node@ nodeWithoutBody_;
Node@ nodeWithBody_;
Node@ nodeWithKinematicBody_;

void Start()
{
    scene_ = Scene();
    scene_.CreateComponent("Octree");

    Node@ cameraNode = scene_.CreateChild("MyCamera");
    cameraNode.CreateComponent("Camera");
    cameraNode.position = Vector3(0.0f, 0.0f, -20.0f);

    Viewport@ viewport = Viewport(scene_, cameraNode.GetComponent("Camera"));
    renderer.viewports[0] = viewport;

    Node@ lightNode = scene_.CreateChild("MyLight");
    Light@ light = lightNode.CreateComponent("Light");
    light.lightType = LIGHT_DIRECTIONAL;
    lightNode.direction = Vector3(0.6f, -0.6f, 0.8f);

    SubscribeToEvent("Update", "HandleUpdate");

    nodeWithoutBody_ = scene_.CreateChild();
    nodeWithoutBody_.position = Vector3(-10.0f, 5.0f, 0.0f);
    StaticModel@ staticModel = nodeWithoutBody_.CreateComponent("StaticModel");
    staticModel.model = cache.GetResource("Model", "Models/Box.mdl");

    nodeWithBody_ = scene_.CreateChild();
    nodeWithBody_.position = Vector3(-10.0f, 0.0f, 0.0f);
    staticModel = nodeWithBody_.CreateComponent("StaticModel");
    staticModel.model = cache.GetResource("Model", "Models/Box.mdl");
    RigidBody@ body = nodeWithBody_.CreateComponent("RigidBody");
    body.mass = 1.0f;
    body.useGravity = false;

    nodeWithKinematicBody_ = scene_.CreateChild();
    nodeWithKinematicBody_.position = Vector3(-10.0f, -5.0f, 0.0f);
    staticModel = nodeWithKinematicBody_.CreateComponent("StaticModel");
    staticModel.model = cache.GetResource("Model", "Models/Box.mdl");
    body = nodeWithKinematicBody_.CreateComponent("RigidBody");
    body.mass = 1.0f;
    body.useGravity = false;
    body.kinematic = true;
}


void HandleUpdate(StringHash eventType, VariantMap& eventData)
{
    float timeStep = eventData["TimeStep"].GetFloat();

    if (input.keyDown[KEY_SPACE])
    {
        nodeWithoutBody_.position = nodeWithoutBody_.position + Vector3(timeStep * 5, 0.0f, 0.0f);
        nodeWithBody_.position = nodeWithBody_.position + Vector3(timeStep * 5, 0.0f, 0.0f);
        nodeWithKinematicBody_.position = nodeWithKinematicBody_.position + Vector3(timeStep * 5, 0.0f, 0.0f);
    }
}
