- VSCode Version: 1.16.1 (1.16.1)
- OS Version: macOS 10.12.6 (16G29)

Steps to Reproduce:

1. Paste this code:
```
// @flow

// without generics
const _select = (map: { [string]: T }): T => {
  if (__DEV__) {
    const supportedKeys = ['phone4', 'phone5'];
    for (const key of Object.keys(map)) {
      if (supportedKeys.indexOf(key) === -1) {
        console.error(`Device.select`);
        return 10;
      }
    }
  }
  if (isPhone4) {
    if (map.phone4 !== undefined) return map.phone4;
    if (map.phone4or5 !== undefined) return map.phone4or5;
  }
};

// with generics
const _select = <T>(map: { [string]: T }): T => {
  if (__DEV__) {
    const supportedKeys = ['phone4', 'phone5'];
    for (const key of Object.keys(map)) {
      if (supportedKeys.indexOf(key) === -1) {
        console.error(`Device.select`);
        return 10;
      }
    }
  }
  if (isPhone4) {
    if (map.phone4 !== undefined) return map.phone4;
    if (map.phone4or5 !== undefined) return map.phone4or5;
  }
};

```
