```ts
  vscode.languages.registerCompletionItemProvider({ pattern: '**/*.brutalo' }, {
        provideCompletionItems(doc, pos) {
            // let count = 6313;
            let count = 20000;
            let result: vscode.CompletionItem[] = [];
            while (count-- >= 0) {
                result.push(new vscode.CompletionItem(String.fromCharCode(97 + (count % 26)) + Math.random().toString(16)));
