```js
// app.js
const { app, BrowserWindow } = require('electron');
let winGood, winHello, winYeah;
app.on('ready', _ => {
    winGood = new BrowserWindow({ x: 100, width: 200, height: 200 });
    winGood.setPosition(100, 100);
    winGood.loadURL(`file://${__dirname}/winGood.html`);
    winGood.on('closed', _ => winGood = null);

    winHello = new BrowserWindow({ x: 600, width: 200, height: 200 });
    winHello.setPosition(100, 300);
    winHello.loadURL(`file://${__dirname}/winHello.html`);
    winHello.on('closed', _ => winHello = null);

    winYeah = new BrowserWindow({ x: 600, width: 200, height: 200 });
    winYeah.setPosition(100, 500);
    winYeah.loadURL(`file://${__dirname}/winYeah.html`);
    winYeah.on('closed', _ => winYeah = null);
});
app.on('window-all-closed', _ => {
    if (process.platform !== 'darwin') {
        app.quit();
    }
});
