- VSCode Version: 1.7.1
- OS Version:  Windows 10 14965

Steps to Reproduce:

1.  Create a text decoration that is applied to where the cursor is (easy example is decoration at line 0, char 0-1 on an empty document)
2.  Start typing
3. Decorator is applied correctly to first letter
4. Delete all text
5. Type again, decorator is applied to everything instead of just first letter

https://gfycat.com/InferiorSlimHerring

```
'use strict';
// The module 'vscode' contains the VS Code extensibility API
// Import the module and reference it with the alias vscode in your code below
import * as vscode from 'vscode';

// this method is called when your extension is activated
// your extension is activated the very first time the command is executed
export function activate(context: vscode.ExtensionContext) {

    var activeEditor = vscode.window.activeTextEditor;
    if (activeEditor) {
        updateDecorations(activeEditor);
    }

    vscode.window.onDidChangeActiveTextEditor(editor => {
        activeEditor = editor;
        if (editor) {
            updateDecorations(activeEditor);
        }
    }, null, context.subscriptions);

    vscode.workspace.onDidChangeTextDocument(event => {
        if (activeEditor && event.document === activeEditor.document) {
            updateDecorations(activeEditor);
        }
    }, null, context.subscriptions);
    vscode.workspace.onDidChangeTextDocument(event => {
        if (activeEditor && event.document === activeEditor.document) {
            updateDecorations(activeEditor);
        }
    }, null, context.subscriptions)
}

function updateDecorations(activeEditor : vscode.TextEditor) {
    if (!activeEditor) {
        return;
    }

    let firstLetterDecoration = vscode.window.createTextEditorDecorationType({color: "#ff0000"});

    let range = new vscode.Range(new vscode.Position(0,0), new vscode.Position(0,1));

    activeEditor.setDecorations(firstLetterDecoration, [range]);
}

// this method is called when your extension is deactivated
export function deactivate() {
}
