_userReducer.spec.js_
```javascript
import {
    expect
} from "chai";
import * as actionTypes from "../actions/actionTypes";
import reducer from "./userReducer";


describe("Reducer::User", () => {
    const getInitialState = () => {
        return {};
    };
    const retrievedResponse = {
        data: {
            username: "testUser",
            firstName: "Foo",
            lastName: "Bar",
            customData: {
                permissions: {
                    inventory: true
                }
            }
        }
    };

    it("should retrieve the initial state if the action type is not registered with the reducer", () => {
        const action = {
            type: "UNKNOWN"
        };

        const expected = getInitialState();
        const actual = reducer(undefined, action); //eslint-disable-line no-undefined

        expect(actual).to.deep.equal(expected);
    });

    it("should retrieve the customData and persist it to the user object", () => {

        const action = {
            type: actionTypes.USER_SET,
            options: retrievedResponse
        };

        const user = reducer(undefined, action); //eslint-disable-line no-undefined
        const actual = user.permissions;

        expect(actual).to.exist; //eslint-disable-line no-unused-expressions

    });

    it("should replace the current state of the user object when USER_SET is dispatched", () => {
        const previousState = {
            permissions: {
                inventory: false
            }
        };

        const futureUserState = {...retrievedResponse,
        permissions: { inventory: true } };

        const action = {
            type: actionTypes.USER_SET,
            options: futureUserState
        };

        const expected = futureUserState.permissions.inventory;

        const user = reducer(previousState, action);
        const actual = user.permissions.inventory;

        expect(actual).to.equal(expected);

            });

        it("should return an empty user object if the data is null", () => {

            const futureUserState = {
                data: null
            };

            const action = {
                type: actionTypes.USER_SET,
                options: futureUserState
            };
            const expected = getInitialState();
            const actual = reducer(undefined, action); //eslint-disable-line no-undefined

            expect(actual).to.deep.equal(expected);

        });

    });
```

