const Chai = require("chai");
const Validation = require("./validation");
const expect = Chai.expect;


    describe("step", () => {
        it("thow excetion if diferent of  string", function (done) {            
            let validation = new Validation();
            var step = { nome: 5 };
            let func = () => { validation.step(step); };

            expect(func).to.throw();
            done();
        });

        it("throw exception if  blank string", function (done) {
        let validation = new Validation();
            let step = { nome: "    " };
            let func = () => {  validation.step(step); };
            expect(func).to.throw();
            done();
        });
    });


