class Validation {
    constructor() {
        this._sequencia = [];
    }


    step(step) {

        if (typeof (step.nome) !== "string" || step.nome.trim() === "") {
            throw Error("Name", 500);
        }

        if (step.condicional && typeof (step.condicional) !== "function") {
            throw Error("Condition", 500);
        }

        if (step.metodo && typeof (step.metodo) !== "function") {
            throw Error("method.", 500);
        }

        this._sequencia.push({ tipo: "etapa", etapa: step });

    }
}
module.exports = Validation;

