'use strict';
import * as vscode from 'vscode';
export function activate(context: vscode.ExtensionContext) {
    let virtualDocumentUri = vscode.Uri.parse('speciale://authority/path');

    let disposable = vscode.commands.registerCommand('extension.sayHello', () => {
        vscode.commands.executeCommand('vscode.previewHtml', virtualDocumentUri).then(success => {
        });
    });
    let counter = 0;
    let eventEmitter = new vscode.EventEmitter<vscode.Uri>();
    let textDocumentContentProvider : vscode.TextDocumentContentProvider = {
        provideTextDocumentContent(uri) {
            return `Content of URI <b>${uri.toString()}</b>` + counter;
        },
        onDidChange: eventEmitter.event
    }

    let registration = vscode.workspace.registerTextDocumentContentProvider('speciale', textDocumentContentProvider);
    setInterval(() => {
        counter++;
        eventEmitter.fire(virtualDocumentUri);
    }, 5000);

    context.subscriptions.push(disposable);
}

